#import <Foundation/Foundation.h>
#import <React/RCTBridgeModule.h>
#import <React/RCTViewManager.h>

@interface RCT_EXTERN_MODULE(DragViewManager, RCTViewManager)

// MARK: - Drag Session
RCT_EXPORT_VIEW_PROPERTY(onDragBegin, RCTBubblingEventBlock)
RCT_EXPORT_VIEW_PROPERTY(onDragOver, RCTBubblingEventBlock)
RCT_EXPORT_VIEW_PROPERTY(onDragEnd, RCTBubblingEventBlock)
RCT_EXPORT_VIEW_PROPERTY(context, NSArray)
@end
