#import <Foundation/Foundation.h>
#import <React/RCTBridgeModule.h>
#import <React/RCTViewManager.h>

@interface RCT_EXTERN_MODULE(DropZoneManager, RCTViewManager)

// MARK: - Drop Session
RCT_EXPORT_VIEW_PROPERTY(onDrop, RCTBubblingEventBlock)
RCT_EXPORT_VIEW_PROPERTY(onProcessingDrop, RCTBubblingEventBlock)
RCT_EXPORT_VIEW_PROPERTY(onDragEnter, RCTBubblingEventBlock)
RCT_EXPORT_VIEW_PROPERTY(onDragLeave, RCTBubblingEventBlock)
RCT_EXPORT_VIEW_PROPERTY(onDragExit, RCTBubblingEventBlock)
RCT_EXPORT_VIEW_PROPERTY(onDragOver, RCTBubblingEventBlock)

// MARK: - Configuration
RCT_EXPORT_VIEW_PROPERTY(allowedDataTypes, NSArray)

@end


