import * as React from 'react';
import {Platform, requireNativeComponent, View} from 'react-native';
import {node} from 'prop-types';

const NativeDragPreview =
  Platform.OS === 'ios' ? requireNativeComponent('DragPreview') : View;

export default function DragPreview({children}) {
  return (
    <NativeDragPreview style={{position: 'absolute'}}>
      {children}
    </NativeDragPreview>
  );
}

DragPreview.propTypes = {
  children: node,
};
